import React, { Component } from 'react';
import Aux from '../../hoc/Aux/Aux';
import TourPackage from './Package/TourPackage';
import classes from './Packages.css';
import axios from 'axios';
import FullPackage from './FullPackage/FullPackage';

class Packages extends Component {
	state = {
		packages: [],
		selectedPackage: [],
		selectedPackageId: null,
	};
	UNSAFE_componentWillMount() {
		axios
			.get('https://jsonplaceholder.typicode.com/posts')
			.then((responce) => {
				this.setState({ packages: responce.data });
			})
			.catch((error) => {
				console.log('somthing getting wrong');
			});

		console.log('[Packages.js] componentWillMount');
	}

	packageClickedHandler = (id) => {
		let allPackages = this.state.packages;
		let post = allPackages.find((item) => item.id === id);
		this.setState({ selectedPackage: post });
		console.log(this.state.selectedPackage);

		alert('you clicked - ' + this.state.selectedPackage);
	};

	render() {
		let packages = this.state.packages.slice(0, 10);
		packages = packages.map((pkg, index) => (
			<li key={index}>
				<TourPackage
					tourPkge={pkg}
					clicked={() => this.packageClickedHandler(pkg.id)}
				/>
			</li>
		));
		return (
			<Aux>
				<div className={classes.Packages}>
					<section>
						<div style={{ textAlign: 'center', margin: '10px' }}>
							<h1>World Tour Packeges</h1>
							Welcome to a soulful experience of vacationing in
							India, the cradle of ancient civilization with rich
							cultural heritage. Experience the sights and sounds
							of its amazing diversity that is embedded in its
							geography, people and their cultures. Explore the
							gifts of nature and the timeless marks of man-kind
							that dot the landscape of this country.
						</div>
					</section>
					<ul className={classes.UL}> {packages} </ul>

					<div
						style={{
							textAlign: 'center',
							margin: '10px',
							float: 'left',
						}}
					>
						<a href='/'>more...</a>
					</div>
					{this.state.selectedPackageId ? (
						<div>
							<h1>Id :- {this.state.selectedPackage.id}</h1>
							<h1>Title :- {this.state.selectedPackage.title}</h1>
							<p>{this.state.selectedPackage.body}</p>
						</div>
					) : (
						<p1>Select package</p1>
					)}
				</div>
			</Aux>
		);
	}
}

export default Packages;
