import React, { Component } from "react";
import Aux from "../../hoc/Aux/Aux";
import TourPackage from "./Package/TourPackage";
import classes from "./Packages.css";
import axios from "axios";
import FullPackage from './FullPackage/FullPackage';

class Packages extends Component {
	state = {

		packages: [

		],
		selectedPackageId: null

	};

	componentDidMount() {

		axios.get("https://jsonplaceholder.typicode.com/posts")
			.then((responce) => {
				const updatedState = [ ...this.state.packages, ...responce.data ];
				this.setState({ packages: updatedState });
			})
			.catch((error) => {
				console.log("somthing getting wrong");
			});

		console.log('[Packages.js] componentDidMount');
	}
//   shouldComponentUpdate(nextProp,nextState){
// 	   if(nextState.packages !== this.state.packages)
//   }
	packageClickedHandler = (id) => {
		this.setState({ selectedPackageId: id });
		alert('you clicked - ' + id);

	}

	render() {
		let packages = this.state.packages.slice(0, 10);
		packages = packages.map((pkg, index) => (
			<li key={index}>
				<TourPackage
					tourPkge={pkg}
					clicked={() => this.packageClickedHandler(pkg.id)}
				/>
			</li>
		));
		return (
			<Aux>
				<div className={classes.Packages}>
					<section>
						<div style={{ textAlign: "center", margin: "10px" }}>
							<h1>World Tour Packeges</h1>
							Welcome to a soulful experience of vacationing in India, the
							cradle of ancient civilization with rich cultural heritage.
							Experience the sights and sounds of its amazing diversity that is
							embedded in its geography, people and their cultures. Explore the
							gifts of nature and the timeless marks of man-kind that dot the
							landscape of this country.
						</div>
					</section>
					<ul className={classes.UL}> {packages} </ul>

					<div style={{ textAlign: "center", margin: "10px", float: "left" }}>
						<a href='/'>more...</a>
					</div>
					<FullPackage id={this.state.selectedPackageId} />
				</div>
			</Aux>
		);
	}
}

export default Packages;
