import React, { Component } from "react";
import Aux from "../../hoc/Aux/Aux";
import TourPackage from "./Package/TourPackage";
import classes from "./Packages.css";
import axios from "axios";
import FullPackage from './FullPackage/FullPackage';

class Packages extends Component {
	state = {

		packages: [
			{
				id: 1,
				title: "Kashmir",
				summery:
					"Kashmir is a state which is equally rich in cultural and natural wonders along with the myriad of history and political past that this state is home to. This state provides unique experiences to its tourists in the form of spicy native food along with a variety of teas, long walks or a jagged trek...",
			},
			{
				id: 2,
				title: "Srinagar",
				summery:
					"Srinagar, the summer seat of the government of the state of Jammu and Kashmir has long been one of IndiaÃ¢s most popular tourist destinations. In part, this has had to do with the multi-layered character of the city and its several attractions. And in part, this is because Srinagar serves as the ba...",
			},
			{
				id: 3,
				title: "Kerala",
				summery:
					"There are dime a dozen Kerala tour packages, but to pick the one that truly suits your interest whether it is nature, history, art, culture, cuisine or leisure, requires discretion. We help you shortlist Kerala holiday packages that are a bang fo....",
			},
			{
				id: 4,
				title: "Manali",
				summery:
					"Himachal Pradesh covers the north front of the country, offering the tourists with some of the most wonderful hill stations in the world and Manali is undoubtedly one of the most visited one. The main credit goes to its snow covered peaks, alluring ambience, gorgeous valleys and lush greenery. It ...",
			},
			{
				id: 4,
				title: "Singapur",
				summery:
					"Himachal Pradesh covers the north front of the country, offering the tourists with some of the most wonderful hill stations in the world and Manali is undoubtedly one of the most visited one. The main credit goes to its snow covered peaks, alluring ambience, gorgeous valleys and lush greenery. It ...",
			},
			{
				id: 5,
				title: "Russia",
				summery:
					"Himachal Pradesh covers the north front of the country, offering the tourists with some of the most wonderful hill stations in the world and Manali is undoubtedly one of the most visited one. The main credit goes to its snow covered peaks, alluring ambience, gorgeous valleys and lush greenery. It ...",
			},
			{
				id: 6,
				title: "Dubai",
				summery:
					"Himachal Pradesh covers the north front of the country, offering the tourists with some of the most wonderful hill stations in the world and Manali is undoubtedly one of the most visited one. The main credit goes to its snow covered peaks, alluring ambience, gorgeous valleys and lush greenery. It ...",
			},
			{
				id: 7,
				title: "Jaipur",
				summery:
					"Himachal Pradesh covers the north front of the country, offering the tourists with some of the most wonderful hill stations in the world and Manali is undoubtedly one of the most visited one. The main credit goes to its snow covered peaks, alluring ambience, gorgeous valleys and lush greenery. It ...",
			},
			{
				id: 7,
				title: "Udaipur",
				summery:
					"Himachal Pradesh covers the north front of the country, offering the tourists with some of the most wonderful hill stations in the world and Manali is undoubtedly one of the most visited one. The main credit goes to its snow covered peaks, alluring ambience, gorgeous valleys and lush greenery. It ...",
			},
			{
				id: 8,
				title: "Shimla",
				summery:
					"Himachal Pradesh covers the north front of the country, offering the tourists with some of the most wonderful hill stations in the world and Manali is undoubtedly one of the most visited one. The main credit goes to its snow covered peaks, alluring ambience, gorgeous valleys and lush greenery. It ...",
			},
		],
		selectedPackageId: null

	};

	componentDidMount() {
		if (this.state.selectedPackageId) {
			axios
				.get("https://jsonplaceholder.typicode.com/posts")
				.then((responce) => {
					const updatedState = [...this.state.packages, ...responce.data];
					this.setState({ packages: updatedState });
				})
				.catch((error) => {
					console.log("somthing getting wrong");
				});
		}
		console.log('[Packages.js] componentDidUpdate');
	}

	packageClickedHandler = (id) => {
		this.setState({ selectedPackageId: id });

	}

	render() {
		let packages = this.state.packages.slice(0, 10);
		packages = packages.map((pkg, index) => (
			<li key={index}>
				<TourPackage
					tourPkge={pkg}
					clicked={() => this.packageClickedHandler(pkg.id)}
				/>
			</li>
		));
		return (
			<Aux>
				<div className={classes.Packages}>
					<section>
						<div style={{ textAlign: "center", margin: "10px" }}>
							<h1>World Tour Packeges</h1>
							Welcome to a soulful experience of vacationing in India, the
							cradle of ancient civilization with rich cultural heritage.
							Experience the sights and sounds of its amazing diversity that is
							embedded in its geography, people and their cultures. Explore the
							gifts of nature and the timeless marks of man-kind that dot the
							landscape of this country.
						</div>
					</section>
					<ul className={classes.UL}>{packages}</ul>

					<div style={{ textAlign: "center", margin: "10px", float: "left" }}>
						<a href='/'>more...</a>
					</div>
					<FullPackage id={this.state.selectedPackageId} />
				</div>
			</Aux>
		);
	}
}

export default Packages;
