import React, { Component } from 'react';
import axios from 'axios';

class FullPackage extends Component {
    state = {
        package: [],
        showPackage: false
    }
    //   showPackageHandler(id){

    //   }
    componentDidMount() {
        console.log('[FullPackage.js] componentDidMount');
    }

    shouldComponentUpdate(prevProp, prevState) {

        if (this.props.id !== prevProp.id) {
            const url = 'https://jsonplaceholder.typicode.com/posts/' + this.props.id;
            axios.get(url).then((response) => {
                console.log(response.data);

                this.setState({package:response.data});
            }).catch((error) => console.log(error))
        }

        console.log('[FullPackage.js] componentShouldUpdate ');
        console.log(prevProp);
        console.log(prevState);
        console.log('------------');

        return true;
    }

    componentDidUpdate(prevProp, prevState) {

        // if (this.props.id) {
        //     const url = 'https://jsonplaceholder.typicode.com/posts/' + this.props.id;
        //     axios.get(url).then((response) => {
        //         console.log(response.data);

        //         this.setState({package:response.data});
        //     }).catch((error) => console.log(error))
        // }
        // console.log(prevProp);
        // console.log(prevState);

        console.log('[FullPackage.js] componentDidUpdate');
    }

    render() {

        let fullPackage = <p>Please Select Package to full Package discription</p>
        if (this.state.showPackage) {
            fullPackage = (
                <div>
                    <h1>Id :- {this.state.package.id}</h1>
                    <h1>Title :- {this.state.package.title}</h1>
                    <p>{this.state.package.body}</p>
                </div>
            );
        }
        return (
            <div>
                {fullPackage}
            </div>
        );
    }
}

export default FullPackage;
