import React, { Component } from 'react';
import Aux from '../../hoc/Aux/Aux';
import TourPackage from './Package/TourPackage';
import classes from './Packages.css';
import axios from 'axios';
class Packages extends Component {
    state = {
        packages: [
            { title: 'Kashmir', summery: 'Kashmir is a state which is equally rich in cultural and natural wonders along with the myriad of history and political past that this state is home to. This state provides unique experiences to its tourists in the form of spicy native food along with a variety of teas, long walks or a jagged trek...' },
            { title: 'Srinagar', summery: 'Srinagar, the summer seat of the government of the state of Jammu and Kashmir has long been one of Indiaâs most popular tourist destinations. In part, this has had to do with the multi-layered character of the city and its several attractions. And in part, this is because Srinagar serves as the ba...' },
